mapper.select:
select
`user_id`,`username`,`name`,`password`,`dept_id`,`email`,`mobile`,`status`,`user_id_create`,`gmt_create`,`gmt_modified`,`sex`,`birth`,`pic_id`,`live_address`,`hobby`,`province`,`city`,`district`,
admin_org_code,admin_org_name,party_org_code,party_org_name,communist_org_code,communist_org_name,trade_union_code,trade_union_name,post_name,office_tel,direct_leader_id,direct_leader_name,creator,updater,company_id
from sys_user

    and user_id = #{userId}
    and username = #{username}
    and name regexp #{name}
    and password = #{password}
    and dept_id = #{deptId}
    and dept_id in

            #{item}


    and email = #{email}
    and mobile = #{mobile}
    and status = #{status}
    and user_id_create = #{userIdCreate}
    and gmt_create = #{gmtCreate}
    and gmt_modified = #{gmtModified}
    and sex = #{sex}
    and birth = #{birth}
    and pic_id = #{picId}
    and live_address = #{liveAddress}
    and hobby = #{hobby}
    and province = #{province}
    and city = #{city}
    and district = #{district}
    and admin_org_code = #{adminOrgCode}
    and admin_org_name regexp #{adminOrgName}
    and party_org_code = #{partyOrgCode}
    and party_org_name regexp #{partyOrgName}
    and communist_org_code = #{communistOrgCode}
    and communist_org_name regexp #{communistOrgName}
    and trade_union_code = #{tradeUnionCode}
    and trade_union_name regexp #{tradeUnionName}
    and post_name = #{postName}
    and office_tel = #{officeTel}
    and direct_leader_name = #{directLeaderName}
    and direct_leader_id = #{directLeaderId}
    and company_id = #{companyId}



        order by ${sort} ${order}


        order by user_id desc



    limit #{offset}, #{limit}

select count(*) from sys_user

    and user_id = #{userId}
    and username = #{username}
    and name = #{name}
    and password = #{password}
    and dept_id = #{deptId}
    and email = #{email}
    and mobile = #{mobile}
    and status = #{status}
    and user_id_create = #{userIdCreate}
    and gmt_create = #{gmtCreate}
    and gmt_modified = #{gmtModified}
    and sex = #{sex}
    and birth = #{birth}
    and pic_id = #{picId}
    and live_address = #{liveAddress}
    and hobby = #{hobby}
    and province = #{province}
    and city = #{city}
    and district = #{district}
    and admin_org_code = #{adminOrgCode}
    and admin_org_name regexp #{adminOrgName}
    and party_org_code = #{partyOrgCode}
    and party_org_name regexp #{partyOrgName}
    and communist_org_code = #{communistOrgCode}
    and communist_org_name regexp #{communistOrgName}
    and trade_union_code = #{tradeUnionCode}
    and trade_union_name regexp #{tradeUnionName}
    and post_name = #{postName}
    and office_tel = #{officeTel}
    and direct_leader_id = #{directLeaderId}
    and direct_leader_name = #{directLeaderName}
    and company_id = #{companyId}

mapper.update:
update sys_user
`username` = #{username}, `name` = #{name}, `password` = #{password}, `dept_id` = #{deptId}, `email` = #{email}, `mobile` = #{mobile}, `status` = #{status}, `user_id_create` = #{userIdCreate}, `gmt_create` = #{gmtCreate}, `gmt_modified` = #{gmtModified}, `sex` = #{sex}, `birth` = #{birth}, admin_org_code = #{adminOrgCode}, admin_org_name = #{adminOrgName}, party_org_code = #{partyOrgCode}, party_org_name = #{partyOrgName}, communist_org_code = #{communistOrgCode}, communist_org_name = #{communistOrgName}, trade_union_code = #{tradeUnionCode}, trade_union_name = #{tradeUnionName}, post_name = #{postName}, office_tel = #{officeTel}, direct_leader_id = #{directLeaderId}, direct_leader_name = #{directLeaderName}, creator = #{creator}, updater = #{updater}, company_id = #{companyId},
where user_id = #{userId}
